import React, { Component } from 'react';
import './App.css';

class App extends Component{
  constructor(props){
    super(props)
    this.state = {
      monsters:[
        {
        name:'Dracula'
        },
        {
          name:'Zombie'
        }
      ]
    }
  }
  componentDidMount() {
    fetch('http://jsonplaceholder.typicode.com/users')
    .then(res => res.json())
    .then(mon => this.setState({
      monsters : mon
    }))
  }

  render(){
    return(
      <div>
        {
          this.state.monsters.map(monster => <h1>{monster.name}</h1>)
        }
      </div>
    )
  }

}

export default App;
