import { CartTypes } from "./cart.types";

const INITIAL_STATE = {
  hidden: true,
};
const cartReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case CartTypes.TOGGLE_HIDDEN:
      console.log(state);
      return {
        ...state,
        hidden: !state.hidden,
      };
    default:
      return state;
  }
};

export default cartReducer;
