import React from "react";

function FormInput({ label, handleChange, ...other }) {
  return (
    <div className="group">
      <input className="form-input" {...other} onChange={handleChange} />
      {label && (
        <label className={`${other.value.length && "shrink"}`}>{label}</label>
      )}
    </div>
  );
}

export default FormInput;
